# Usa este script SOLO si puedes conectarte a MongoDB desde donde lo ejecutas.# Si usas Docker Compose, ejecuta el script DENTRO del contenedor backend:# docker compose exec backend python app/scripts/validate_roles_descriptions.pyimport pymongoMONGO_URI = "mongodb://mongo:27017"  # Cambia 'mongo' por el nombre de tu servicio en docker-composeDB_NAME = "excel"  # Cambia si tu base se llama diferentedef main():    client = pymongo.MongoClient(MONGO_URI)    db = client[DB_NAME]    roles = list(db.roles.find({}))    print("Roles encontrados en la base de datos:\n")    for role in roles:        print(f"- name: {role.get('name')}, description: {role.get('description')}, permissions: {role.get('permissions')}")        if not role.get('description'):            print("  >>> ERROR: Este rol NO tiene descripci√≥n <<<")    print("\nTotal roles:", len(roles))    client.close()if __name__ == "__main__":    main()